(2303, 1009, 1686, 1686, 25, 'Fehlermeldung: Operation muss eine aktualisierbare Abfra', 'Hallo Leute, ich habe ebenfalls die oben genannte Fehlermeldung.\r\nIch habe es schon mit und ohne OleDbDataAdapter probiert...aber es geht einfach nicht. Die Parameter hab ich auch mit ? abgegeben...vielleicht fehlt ein Namespace??? Ich hab schon alles probiert. Hier der Quellcode:\r\n\r\n\r\nPrivate Sub bttSpeichern_Click(ByVal sender As System.Object, ByVal e As System.EventArgs) Handles bttSpeichern.Click\r\n        \r\n        Dim Conn As OleDbConnection\r\n        Dim cmd As OleDbCommand\r\n        Dim strID As String\r\n        Dim adapter As OleDbDataAdapter\r\n        Dim cmdID As OleDbCommand\r\n        Dim lngID As Long\r\n        Dim reader As OleDbDataReader\r\n\r\n        Conn = New OleDbConnection(ConfigurationSettings.AppSettings(\"Kosten_ConnectionString\"))\r\n        Conn.Open()\r\n\r\n        \' ID des Datensatzes ermitteln und in Long umwandeln\r\n        strID = \"SELECT DMax(\"\"Mitarbeiterkosten.mk_nr\"\", \"\"Mitarbeiterkosten\"\") As MaxID FROM Mitarbeiterkosten\"\r\n        cmdID = New OleDbCommand(strID, Conn)\r\n        reader = cmdID.ExecuteReader()\r\n        If reader.Read() = True Then\r\n            lngID = System.Convert.ToInt64(reader.GetValue(0))\r\n        End If\r\n        reader.Close()\r\n\r\n        cmd = New OleDbCommand()\r\n        cmd.CommandType = CommandType.Text\r\n        cmd.CommandText = \"INSERT INTO Mitarbeiterkosten (mk_nr, mk_bez) VALUES (?, ?)\"\r\n        cmd.Connection = Conn\r\n\r\n        cmd.Parameters.Add(\"mk_nr\", lngID)\r\n        cmd.Parameters.Add(\"mk_bez\", Me.mk_bez.Text)\r\n  \r\n        adapter = New OleDbDataAdapter()\r\n        adapter.InsertCommand = cmd\r\n\r\n        cmd.ExecuteNonQuery()\r\n\r\n        Conn.Close()\r\n    End Sub\r\n\r\n\r\nIch habe ebenfalls eine Access 2000 Datenbank zugrunde. Bitte helfen Sie mir.\r\nMfG Alexander', '2003-06-18 10:48:04'),
(2304, 1009, 1686, 2303, 331, 'Fehlermeldung: Operation muss eine aktualisierbare A', 'Eine L?sung fand ich unter folgendem Link:\r\n\r\nhttp://www.aspfaq.de/index.asp?RID=1&FID=123&SFI=1&ELE=10708&OPID=0&ORID=0&OSST=Operation+muss+eine+aktualisierbare+Abfrage+verwen&OSFI=1\r\n\r\nViele Gr??e\r\nEckhard Hagemeier', '2003-06-18 12:13:25'),
(2305, 1009, 2305, NULL, 331, 'ODBC-Objekt mit mySQL', 'Folgendes Script l?uft auf meinem PC mit richtigem Ergebnis aber leider nicht auf dem Server: Fehlermeldung: no data \r\n\r\nDim conO As OdbcConnection = New OdbcConnection() \r\nconO.ConnectionString = \"driver={MySQL ODBC 3.51 Driver};server=ntipx1.ecs-webhosting.de;database=...;UID=...;Password=...;\" \r\nconO.Open() \r\nDim sql As String = \"Select * from newsdiverse\" \r\nDim cmd As New OdbcCommand(sql, conO) \r\nDim myreader As OdbcDataReader \r\nmyreader = cmd.ExecuteReader \r\ndataNews.DataSource = myreader \r\ndataNews.DataBind() \r\nmyreader.Close() \r\nconO.Close() \r\n\r\nWoran liegts? \r\n\r\nGibt es irgendwo Informationen bzw. Fachleute f?r die Datenbindung mySQL -> ASP .net?\r\n\r\nF?r eine Antwort w?re ich sehr dankbar!\r\nViele Gr??e\r\nEckhard Hagemeier', '2003-06-18 12:17:17'),
